package in.folknet.folkbloreattendance;import android.app.Activity;import android.content.Intent;//import android.support.v7.app.Activity;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.Button;import android.widget.EditText;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.Spinner;import android.widget.Toast;public class Guest extends Activity {EditText name,mNo, jp;    Button btn;    RadioButton rb;    RadioGroup rg;    Spinner spinner_area;    String selectedItemText;    String tname,mNum,ws,mNumber, mjp;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_guest);        name = (EditText)findViewById(R.id.name);        mNo = (EditText)findViewById(R.id.mno);        btn = (Button)findViewById(R.id.btn);        rg = (RadioGroup)findViewById(R.id.radioGroup);        jp = (EditText)findViewById(R.id.etjp);        spinner_area = (Spinner) findViewById(R.id.spinner_area);        spinner_area.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {            @Override            public void onItemSelected(AdapterView<?> parent, View view, int position, long lid) {                selectedItemText = spinner_area.getItemAtPosition(position).toString();                // Notify the selected item text                Toast.makeText                        (getApplicationContext(), "Selected : " + selectedItemText, Toast.LENGTH_SHORT)                        .show();            }            @Override            public void onNothingSelected(AdapterView<?> parent) {            }        });        btn.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                tname = name.getText().toString();                mNum = mNo.getText().toString();                mjp = jp.getText().toString();                int selectedId = 0;                selectedId = rg.getCheckedRadioButtonId();                rb = (RadioButton) findViewById(selectedId);                if (tname.isEmpty()){                    Toast.makeText(getApplicationContext(), "Enter Name!", Toast.LENGTH_SHORT).show();                }else if (mNum.isEmpty()||mNum.length() < 10){                    Toast.makeText(getApplicationContext(), "Enter Correct Mobile No.!", Toast.LENGTH_SHORT).show();                }else if (selectedId == -1) {                    Toast.makeText(getApplicationContext(), "Select FOLK Guide!", Toast.LENGTH_SHORT).show();                }else if(mjp.isEmpty()){                    Toast.makeText(getApplicationContext(), "You have not entered Japa Rounds!", Toast.LENGTH_SHORT).show();                }else if(selectedItemText.equals("Not Selected")){                    Toast.makeText(getApplicationContext(), "You have not selected any Area near to your Residence!", Toast.LENGTH_SHORT).show();                }                else{                    //validation is complated                    ws = rb.getText().toString();                    switch (ws){                        case "SELF EMPOWERMENT" :                            ws = "SELF EMPOWERMENT";                            break;                        case "HAPPINESS WORKSHOP" :                            ws = "HAPPINESS WS";                            break;                        case "SPECIAL SESSION":                            ws = "SPECIAL SESSION";                            break;                        case "SERVICE":                            ws = "SERVICE";                            break;                        default:                    }                    Toast.makeText(getApplicationContext(), ws, Toast.LENGTH_SHORT).show();                    Intent intent = new Intent(Guest.this, FolkGuide.class);                    intent.putExtra("name", tname);                    intent.putExtra("mobNo",mNum);                    intent.putExtra("mjp",mjp);                    intent.putExtra("ws",ws);                    intent.putExtra("mnum",mNum);                    intent.putExtra("selected_area", selectedItemText);                    startActivity(intent);                }            }        });    }}