package in.folknet.folkbloreattendance;import android.app.Activity;import android.content.Intent;//import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.ImageButton;public class InternetConnection extends Activity {ImageButton refresh;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_internet_connection);        refresh = (ImageButton)findViewById(R.id.refresh);        refresh.setOnClickListener(new View.OnClickListener(){            @Override            public void onClick(View v) {                Intent intent = new Intent(InternetConnection.this, Main2Activity.class);                startActivity(intent);            }});    }}