package in.folknet.folkbloreattendance;import android.app.Activity;import android.content.Intent;//import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;public class Main2Activity extends Activity {    Button b1,b2;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main2);        b1=(Button)findViewById(R.id.b1);        b2=(Button)findViewById(R.id.b2);        b1.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), FOLK.class);                startActivity(intent);            }        });        b2.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(getApplicationContext(), Guest.class);                startActivity(intent);            }        });    }}